import { useEffect, useState } from "react";
import { Grid, Box, Typography } from "@mui/material";
import { Link, useSearchParams } from "react-router-dom";
import { API } from "../../../service/api";
import Post from "./Post";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [allPosts, setAllPosts] = useState([]);
  const [searchParams] = useSearchParams();
  const category = searchParams.get("category");

  useEffect(() => {
    const fetchData = async () => {
      const response = await API.getAllPosts({ category: category || "" });
      if (response.isSuccess) {
        setPosts(response.data);
      }

      const allPostsResponse = await API.getAllPosts();
      if (allPostsResponse.isSuccess) {
        setAllPosts(allPostsResponse.data);
      }
    };
    fetchData();
  }, [category]);

  return (
    <>
      {posts?.length ? (
        posts.map((post) => (
          <Grid item lg={3} sm={4} xs={12} key={post._id}>
            <Link
              style={{ textDecoration: "none", color: "inherit" }}
              to={`details/${post._id}`}
            >
              <Post post={post} />
            </Link>
          </Grid>
        ))
      ) : (
        <Box
          style={{
            color: "878787",
            margin: "30px 80px",
            fontSize: 18,
          }}
        >
          <Typography variant="h5">
            No data is available for the selected category
          </Typography>
        </Box>
      )}

      {/* Display posts for "All categories" */}
      {category === null && allPosts?.length
        ? allPosts.map((post) => (
            <Grid item lg={3} sm={4} xs={12} key={post._id}>
              <Link
                style={{ textDecoration: "none", color: "inherit" }}
                to={`details/${post._id}`}
              >
                <Post post={post} />
              </Link>
            </Grid>
          ))
        : null}
    </>
  );
};

export default Posts;
